"use strict";

var express = require("express");
var path = require("path");
var diaryRoutes = require("./routes/diariesRoutes");
var userRoutes = require("./routes/userRoutes");
var globalErrorHandler = require("./controller/errorController");
var rateLimit = require("express-rate-limit");
var AppError = require("./util/appError");
var cookieParser = require("cookie-parser");

//express app
var app = express();

// Set the view engine to Pug
app.set("view engine", "pug");
app.set("views", path.join(__dirname, "views"));

//serving static files from the backend
app.use("/static", express["static"](path.join(__dirname, "public")));

//trust the first hop
app.set("trust proxy", 1);

// limiting api request
var limiter = rateLimit({
  max: 500,
  windowMs: 60 * 60 * 1000,
  message: "Too many request from this IP , please try again in an hour!"
});
app.use("/api", limiter);
app.get("/api/v1/user/resetPasswordPage/:resetToken", function (req, res) {
  res.sendFile(path.resolve(__dirname, "ResetPasswordPage.html"));
});

//body parser
app.use(express.json({
  limit: "10kb"
}));
app.use(cookieParser());
app.use(function (req, res, next) {
  console.log(req.path, req.method);
  next();
});
//routes
app.use("/api/v1/diary", diaryRoutes);
app.use("/api/v1/user", userRoutes);
app.get("/api/v1/userphoto/:filename", function (req, res) {
  var filename = req.params.filename;
  res.sendFile(path.join(__dirname, "public/img/users", filename));
});

//favicon
app.use(function (req, res, next) {
  if (req.originalUrl && req.originalUrl.split("/").pop() === "favicon.ico") {
    return res.sendStatus(204);
  }
  next();
});

//error for unknown routes
app.all("*", function (req, res, next) {
  res.status(404).json({
    status: "fail",
    message: "Cant find ".concat(req.originalUrl, " on this server!")
  });
  var err = new Error("Cant find ".concat(req.originalUrl, " on this server!"));
  err.status = "fail";
  err.statusCode = 404;
  next(new AppError("Cant find ".concat(req.originalUrl, " on this server!"), 404));
});
app.use(globalErrorHandler);
module.exports = app;
