const nodemailer = require("nodemailer");
const htmlToText = require("html-to-text");
const React = require("react");
const ReactDOMServer = require("react-dom/server");
const WelcomeEmail = require("../../public/js/welcome");
const ResetPasswordEmail = require("../../public/js/resetPassword");

// new Email(user, url).sendWelcome();

module.exports = class Email {
  constructor(user, url) {
    this.to = user.email;
    this.firstName = user.name.split(" ")[0];
    this.url = url;
    this.from = `<${process.env.EMAIL_FROM}>`;
  }

  newTransport() {
    return nodemailer.createTransport({
      // service: 'Brevo',
      host: process.env.SENDINBLUE_HOST,
      port: process.env.SENDINBLUE_PORT,
      auth: {
        user: process.env.SENDINBLUE_LOGIN,
        pass: process.env.SENDINBLUE_PASSWORD,
      },
    });
  }

  //send the actual email

  async send(type, subject) {
    // Render the React component to HTML
    let reactHtml;
    if (type === "welcome") {
      reactHtml = ReactDOMServer.renderToStaticMarkup(
        React.createElement(WelcomeEmail, {
          firstName: this.firstName,
          url: this.url,
          subject,
        })
      );
    }
    if (type === "resetPassword") {
      reactHtml = ReactDOMServer.renderToStaticMarkup(
        React.createElement(ResetPasswordEmail, {
          firstName: this.firstName,
          url: this.url,
          subject,
        })
      );
    }

    //define e mail options
    const mailOptions = {
      from: this.from,
      to: this.to,
      subject,
      html: reactHtml,
      text: htmlToText.convert(reactHtml),
      //html
    };

    //3 create a trasnport and send email
    await this.newTransport().sendMail(mailOptions);
  }

  async sendWelcome() {
    await this.send("welcome", "Welcome to the S.Diary family");
  }

  async sendPasswordReset() {
    await this.send(
      "resetPassword",
      "Your password reset token valid for only 10 mins"
    );
  }
};
